    {
  click: function(e) {
    e.preventDefault();

    var uri = $(this.Element).attr("href");
    xTags.RemoveUriEntry(uri);
    for(var i = 0; i < this.MainTag.ObjervableEntries.length; ++i) {
      if (this.MainTag.ObjervableEntries[i].uri() == uri) {
        this.MainTag.ObjervableEntries.splice(i, 1);
        --i;
      }
    }
  }
